import DeleteIcon from "../../icons/delete.svg";
import OkIcon from "../../icons/ok.svg";

export default function Todo({ name, description, handleNameClick }) {

    const clickOnName = ()=>{
        handleNameClick()
    }


  return (
    <div className="todo">
      <div className="todo-info-block">
        <p className="todo-name" onClick={clickOnName}>{name}</p>
        <p className="todo-description">{description}</p>
      </div>
      <div className="todo-block-buttons">
        <button type="button" className="button">
          <img className="icon" src={OkIcon} alt="сделано" />
        </button>
        <button type="button" className="button">
          <img className="icon" src={DeleteIcon} alt="удалить" />
        </button>
      </div>
    </div>
  );
}
