import { useState } from "react";
import Popup from "./components/popup/Popup";
import Todo from "./components/Todo/Todo";
import AddIcon from "./icons/add.svg";
import SerachIcon from "./icons/search.svg";
import todos from "./utils";
import FormAdd from "./components/form/FormAdd";
import FormCard from "./components/form/FormCard";

function App() {
  const [isOpenAdd, setIsOpenAdd] = useState(true);
  const [isOpenCard, setIsOpenCard] = useState(false);
  const [infoPopup, setInfoPopup] = useState({})

  const handleAddTodo = () => {
    setIsOpenAdd(true);
  };

  const handleClosePopups = () => {
    setIsOpenAdd(false);
    setIsOpenCard(false);
  };

  const handleOpenTodo = ({info}) => {
    setIsOpenCard(true);
    setInfoPopup()

  };
  return (
    <div className="page__container">
      <h1 className="title">Todo List</h1>
      <div className="setting-block">
        <div className="search-block">
          <input type="text" placeholder="Найти дело" className="input" />
          <button type="button" className="button">
            <img src={SerachIcon} alt="найти" className="icon" />
          </button>
        </div>
        <button className="button" type="button" onClick={handleAddTodo}>
          <img src={AddIcon} alt="добавить" className="icon" />
        </button>
      </div>
      <div className="todos-container">
        <ul className="todos-list">
          {todos.map((todo) => (
            <li key={todo.id} className="todo-item">
              <Todo
                name={todo.name}
                description={todo.description}
                handleNameClick={handleOpenTodo}
              />
            </li>
          ))}
        </ul>
      </div>
      <Popup
        isOpen={isOpenAdd}
        onClose={handleClosePopups}
        title="Добавить дело"
        children={<FormAdd />}
      />
      <Popup
        isOpen={isOpenCard}
        onClose={handleClosePopups}
        title="Редактировать дело"
        children={<FormCard />}
        info={infoPopup}
      />
    </div>
  );
}

export default App;
