import DeleteIcon from "../../icons/delete.svg";
import OkIcon from "../../icons/ok.svg";
import "./todo.css";

export default function Todo({ name, description, handleNameClick, status, date, onDelete }) {
  const clickOnName = () => {
    const info={
      name: name,
      description: description,
      status: status,
      date: date
    }
    handleNameClick(info);
  };

  return (
    <div className="todo">
      <div className="todo-info-block">
        <p className="todo-name" onClick={clickOnName}>
          {name}
        </p>
        <p className="todo-description">{description}</p>
        <p className="todo-description-date">{date}</p>
      </div>
      <div className="todo-block-buttons">
        <button type="button" className="button">
          <img className="icon" src={OkIcon} alt="сделано" />
        </button>
        <button type="button" className="button" onCli>
          <img className="icon" src={DeleteIcon} alt="удалить" />
        </button>
      </div>
    </div>
  );
}
