import CloseIcon from '../../icons/close.svg'

export default function PopupWithForm({name, onClose, title, onSubmit}){
  
    return (
      <section
        className={`popup popup_type_${name} ${
          this.props.isOpen ? "popup_opened" : ""
        }`}
        id={`popup-${name}`}
      >
        <div className="popup__container">
          <button
            className="popup__btn"
            onClick={onClose}
            type="reset"
          >
            <img className="popup__icon" src={CloseIcon} alt="иконка крестик" />
          </button>
          <h2 className="popup__title">{title}</h2>
          <form
            className="popup__form"
            name={name}
            onSubmit={onSubmit}
          >
            {this.props.children}
            <button className="popup__submit" type="submit" value="Сохранить">
              {this.props.nameButton}
            </button>
          </form>
        </div>
      </section>
    );
  }
